import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  accessToken: '',
  refreshToken: '',
  bearer: '',
};

export const loginSlice = createSlice({
  name: 'login',
  initialState: initialState,
  reducers: {
    changeAccToken(state, action) {
      state.accessToken = action.payload;
    },
    changeRefToken(state, action) {
      state.refreshToken = action.payload;
    },
    setBearer(state, action) {
      state.bearer = 'Bearer ' + action.payload;
    },
    isLogged
  },
});

export const { changeAccToken, changeRefToken, setBearer } = loginSlice.actions;

export default loginSlice.reducer;
