import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  username: '',
  logged: false,
  acc: ''
};

export const loginSlice = createSlice({
  name: 'login',
  initialState: initialState,
  reducers: {
    setUsername(state, action) {
      state.username = action.payload;
    },
    logUser(state, action) {
      state.logged = action.payload;
      try { 
        state.acc = localStorage.getItem('accToken')
      } catch(err) {
        console.log(err)
      }
      if (state.acc !== '') {
        state.logged = true
        return true
      } else {
        return false
      }
    },
    isLogged(state) {

    }
  },
});

export const { setUsername, logUser, isLogged } = loginSlice.actions;

export default loginSlice.reducer;
