import React from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './SignUp.module.scss';

function matchPsw(one, two) {
    if (one === two) {
        return false
    } else {
        return true
    }
}

export const SignUp = () => {
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail] = useState('')
    const [pasw1, setPasw1] = useState('')
    const [pasw2, setPasw2] = useState('')


    async function register (f, l, e, p) {
        var logInfo = {
            "firstName": f,
            "lastName": l,
            "email": e,
            "password": p,
            "username": "volodya"
        }
        console.log(logInfo)
        var formBody = [];
        for (var key in logInfo) {
          var encodedKey = encodeURIComponent(key); // coding key & value to avoid unexpected errors
          var encodedValue = encodeURIComponent(logInfo[key]); 
          formBody.push(encodedKey + "=" + encodedValue);
        }
        formBody = formBody.join("&");
        console.log(formBody)

        try {
            await fetch('https://society-capital.herokuapp.com/api/signup', {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: formBody
            })
              .then((res) => console.log(res.text()))

        } catch(err) {
            console.log(err)
        }
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.container}>
                <div className={styles.content}>
                    <div className={styles.text}>
                        Sign up
                    </div>
                    <div className={styles.form}>
                        <div className={styles.formOne}>
                            <input placeholder='First Name' value={firstName} onChange={event => setFirstName(event.target.value)}></input>
                            <input placeholder='Last Name' value={lastName} onChange={event => setLastName(event.target.value)}></input>
                        </div>
                        <div className={styles.formTwo}>
                            <input placeholder='E-mail' type='email' value={email} onChange={event => setEmail(event.target.value)}></input>
                        </div>
                        <div className={styles.formThree}>
                            <input autoComplete='off' placeholder='Password' type='password' value={pasw1} onChange={event => setPasw1(event.target.value)}></input>
                            <input autoComplete='off' placeholder='Repeat Password' type='password' value={pasw2} onChange={event => setPasw2(event.target.value)}></input>
                        </div>
                    </div>
                    {matchPsw(pasw1, pasw2) && <div className={styles.match}>The entered passwords must match!</div>}
                    <button onClick={() => register(firstName, lastName, email, pasw2)}>Submit</button>
                    <hr></hr>
                    <div className={styles.haveAcc}>
                        <Link to='/login'><b>I already have an account!</b></Link>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SignUp;
