import React from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './Login.module.scss';

export const Login = () => {
    const [email, setEmail] = useState('')
    const [pasw, setPasw] = useState('')
    
    function getRefToken(text) {
        var refToken = text
        var a = text.indexOf('"refresh_token":"') + 17
        refToken = refToken.slice(a, -2)
        return refToken
    }

    function getAccToken(text) {
        var accToken = text
        var a = accToken.indexOf('"access_token":"') + 16
        accToken = accToken.slice(a, -1)
        var b = accToken.indexOf('"')
        accToken = accToken.slice(0 , b)
        return accToken
    }

    var logInfo = {
        'username': email,
        'password': pasw
    }

    var formBody = [];
    for (var key in logInfo) {
      var encodedKey = encodeURIComponent(key); // coding key & value to avoid unexpected errors
      var encodedValue = encodeURIComponent(logInfo[key]); 
      formBody.push(encodedKey + "=" + encodedValue);
    }
    formBody = formBody.join("&");

    
    async function some () {
        var tokens = ''
        try {
            await fetch('https://society-capital.herokuapp.com/api/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: formBody
            })
            .then((response) => response.text())
            .then(rawTokens => tokens = rawTokens)
        } catch(err) {
            console.log(err)
        }

        console.log(tokens)
        var accToken = getAccToken(tokens);
        var refToken = getRefToken(tokens);
        var bearer = 'Bearer ' + accToken
        console.log(accToken)

        try {
            console.log(bearer)
            await fetch('https://society-capital.herokuapp.com/api/users', {
                method: 'GET',
                mode: 'no-cors',
                headers: {
                    'Authorization': accToken
                }
            }).then(res => res.json())
        } catch(err) {
            console.log(err)
        }
    }
        

    return (
        <div className={styles.wrapper}>
            <div className={styles.container}>
                <div className={styles.content}>
                    <div className={styles.text}>
                        Sign in
                    </div>
                    <div className={styles.form}>
                        <input autoComplete='off' placeholder='Email' value={email} onChange={event => setEmail(event.target.value)}></input>
                        <input autoComplete='off' placeholder='Password' value={pasw} onChange={event => setPasw(event.target.value)}></input>
                    </div>
                    <button onClick={() => some()}>Submit</button>
                    <hr></hr>
                    <div className={styles.signUp}>
                        Don't have an account?
                        <Link to='signup'><button>Sign up</button></Link>
                    </div>
                    <div className={styles.forgot}>
                        <Link to='/not-found'>Forgot your password?</Link>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login;
